import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import DatePicker from 'react-datepicker';
import { format } from 'date-fns';
import { FaCalendar, FaFlag, FaTags, FaPlus, FaTimes, FaCheck } from 'react-icons/fa';
import "react-datepicker/dist/react-datepicker.css";
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [priority, setPriority] = useState('medium');
  const [category, setCategory] = useState('general');
  const [notes, setNotes] = useState('');
  const [filter, setFilter] = useState('all');
  const [showAddForm, setShowAddForm] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/todos');
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;

    try {
      const response = await axios.post('http://localhost:5000/api/todos', {
        text: newTodo,
        dueDate: selectedDate,
        priority,
        category,
        notes
      });
      setTodos([response.data, ...todos]);
      setNewTodo('');
      setSelectedDate(null);
      setPriority('medium');
      setCategory('general');
      setNotes('');
      setShowAddForm(false);
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const toggleTodo = async (id) => {
    try {
      const todo = todos.find(t => t._id === id);
      const response = await axios.put(`http://localhost:5000/api/todos/${id}`, {
        completed: !todo.completed
      });
      setTodos(todos.map(todo => 
        todo._id === id ? response.data : todo
      ));
    } catch (error) {
      console.error('Error toggling todo:', error);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/todos/${id}`);
      setTodos(todos.filter(todo => todo._id !== id));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  return (
    <div className="App">
      <div className="container">
        <motion.h1
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          ✨ Todo List
        </motion.h1>

        <motion.div
          className="filters"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.2 }}
        >
          <button
            className={filter === 'all' ? 'active' : ''}
            onClick={() => setFilter('all')}
          >
            All
          </button>
          <button
            className={filter === 'active' ? 'active' : ''}
            onClick={() => setFilter('active')}
          >
            Active
          </button>
          <button
            className={filter === 'completed' ? 'active' : ''}
            onClick={() => setFilter('completed')}
          >
            Completed
          </button>
        </motion.div>

        <AnimatePresence>
          {showAddForm && (
            <motion.form
              className="todo-form expanded"
              onSubmit={addTodo}
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3 }}
            >
              <input
                type="text"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="What needs to be done?"
                className="todo-input"
              />
              <div className="form-row">
                <div className="form-group">
                  <FaCalendar />
                  <DatePicker
                    selected={selectedDate}
                    onChange={date => setSelectedDate(date)}
                    placeholderText="Due date"
                    className="date-picker"
                  />
                </div>
                <div className="form-group">
                  <FaFlag />
                  <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                  </select>
                </div>
                <div className="form-group">
                  <FaTags />
                  <input
                    type="text"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    placeholder="Category"
                  />
                </div>
              </div>
              <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Add notes..."
                className="notes-input"
              />
              <div className="form-actions">
                <button type="button" onClick={() => setShowAddForm(false)} className="cancel-button">
                  <FaTimes /> Cancel
                </button>
                <button type="submit" className="add-button">
                  <FaCheck /> Add Todo
                </button>
              </div>
            </motion.form>
          )}
        </AnimatePresence>

        {!showAddForm && (
          <motion.button
            className="add-todo-button"
            onClick={() => setShowAddForm(true)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaPlus /> Add New Todo
          </motion.button>
        )}

        <motion.div className="todo-list" layout>
          <AnimatePresence>
            {filteredTodos.map(todo => (
              <motion.div
                key={todo._id}
                className={`todo-item ${todo.completed ? 'completed' : ''} priority-${todo.priority}`}
                layout
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, x: -300 }}
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.2 }}
              >
                <div className="todo-content">
                  <label className="checkbox-container">
                    <input
                      type="checkbox"
                      checked={todo.completed}
                      onChange={() => toggleTodo(todo._id)}
                    />
                    <motion.span
                      className="checkmark"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                    ></motion.span>
                  </label>
                  <div className="todo-details">
                    <span className="todo-text">{todo.text}</span>
                    {todo.dueDate && (
                      <span className="todo-due-date">
                        Due: {format(new Date(todo.dueDate), 'MMM d, yyyy')}
                      </span>
                    )}
                    {todo.category !== 'general' && (
                      <span className="todo-category">{todo.category}</span>
                    )}
                  </div>
                </div>
                <motion.button
                  onClick={() => deleteTodo(todo._id)}
                  className="delete-button"
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  ×
                </motion.button>
              </motion.div>
            ))}
          </AnimatePresence>
        </motion.div>

        {todos.length > 0 && (
          <motion.div
            className="todo-footer"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.3 }}
          >
            <span>{todos.filter(todo => !todo.completed).length} items left</span>
          </motion.div>
        )}
      </div>
    </div>
  );
}

export default App;
